import base64

YELLOW = "\033[93m"
GREEN = "\033[92m"
RED = "\033[91m"
RESET = "\033[0m"

print(" ")
print(f"{YELLOW}::: WELCOME TO BASE64 DECODER ::: {RESET}")
print(" ")
# base64_text = ""
base64_text = input(f"{RED}Enter your Base64 : {RESET}")
try:
    decoded_text = base64.b64decode(base64_text).decode('utf-8')
except UnicodeDecodeError:
    decoded_text = base64.b64decode(base64_text).decode('latin-1')

print(f"{GREEN}The Decoded Text is : {RESET} \n {decoded_text} ")
